import { Link, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const CountryDetails = () => {
    const {name}=useParams();
    const {data, error, isLoding}=useFetch(`https://restcountries.com/v2/name/${name}`);
    
    return ( 
        <div className="pt-28 country-details">
            {isLoding && <div className="text-6xl text-center pt-8 h-screen">Data is loading</div>}
            {error && <div className="text-6xl text-center text-red-500 pt-8 h-screen">{error}</div> }
            <Link to={'/'}>
                <button className="bg-white p-4">Back</button>
            </Link>
            {data &&
                <div className="country flex">
                    <img src={data[0].flag} alt="" className="w-96"/>
                    <div className="details">
                        <div className="name font-extrabold text-3xl">{data[0].name}</div>
                        <span>
                            Native Name: <span>{data[0].nativeName}</span>
                        </span>
                        <span>
                            Top Level Domaine: <span>{data[0].topLevelDomain}</span>
                        </span>
                        <span>
                            Population: <span>{data[0].population.toLocaleString('en-US')}</span>
                        </span>
                        <span>
                            Currencies:
                            <span> 
                                {
                                    data[0].currencies.map(currency=>(
                                        currency.name
                                    ))
                                }
                            </span>
                        </span>
                        <span>
                            Region: <span>{data[0].region}</span>
                        </span>
                        <span>
                            Languages:
                            <span>
                                {
                                    data[0].languages.map(language=>(
                                        language.name+', '
                                    ))
                                }
                            </span>
                        </span>
                        <span>
                            Sub Region: <span>{data[0].subregion}</span>
                        </span>
                        <span>
                            Capital: <span>{data[0].capital}</span>
                        </span>
                    </div>
                </div>
            }
        </div>
     );
}
 
export default CountryDetails;
