import { useState, useEffect } from "react";

const useFetch = (url) => {
    const[data, setData]=useState(null);

    useEffect(()=>{
        const abortCont = new AbortController();

        setTimeout(() => {
            fetch(url)
            .then(res=>{
                res.json();
            })
            .then(data=>{
                setData(data);
            })
        }, 1000);
        return () => abortCont.abort();

    },[url])

    return{data}

}
 
export default useFetch;
