import CountriesList from "./CountriesList";
import { useState } from "react";
import useFetch from "./useFetch";

const Home = () => {
    const [x,setX]=useState('all')
    const {data, error, isLoding}=useFetch(`https://restcountries.com/v2/${x}`);
    
    const handleChange=(value)=>{
        if(value!=''){
            setX(`name/${value}`)
        }else{
            setX('all')
        }
    }
    
    return ( 
        <div className="home bg-vlg px-8 pt-28">
            <div className="px-8 relative flex justify-between mb-6">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-5 h-5 absolute top-2/4 -translate-y-2/4 z-10 left-14">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M21 21l-5.197-5.197m0 0A7.5 7.5 0 105.196 5.196a7.5 7.5 0 0010.607 10.607z" />
                </svg>
                <input type="text" className="drop-shadow-md outline-0 py-4 px-16 w-4/12" placeholder="Search for a country..." onChange={(e)=>handleChange(e.currentTarget.value)}/>
                <div className="bg-white drop-shadow-md p-4 relative">
                    Filter by Region
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-4 h-4 inline ml-8">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 8.25l-7.5 7.5-7.5-7.5" />
                    </svg>
                    <ul className="drop-down absolute bg-white">
                        <li>Africe</li>
                        <li>America</li>
                        <li>Asia</li>
                        <li>Europe</li>
                        <li>Oceania</li>
                    </ul>
                </div> 
            </div>
            {isLoding && <div className="text-6xl text-center pt-8 h-screen">Data is Loading</div>}
            {error && <div className="text-6xl text-center text-red-500 pt-8 h-screen">{error}</div> }
            {data && <CountriesList data={data}/>}        
        </div>
     );
}
 
export default Home;
