import CountriesList from "./CountriesList";
import { useState } from "react";
import useFetch from "./useFetch";

const Home = () => {
    const [x,setX]=useState('all')
    const {data, error, isLoding}=useFetch(`https://restcountries.com/v2/${x}`);
        
   
    return ( 
        <div className="home bg-vlg px-8 pt-28">
            <div className="px-8">
                <input type="text" className="drop-shadow-md py-4 w-3/12" placeholder="Search for a country"/>
            </div>
            {isLoding && <div className="text-6xl text-center pt-8 h-screen">Data is Loading</div>}
            {error && <div className="text-6xl text-center text-red-500 pt-8 h-screen">{error}</div> }
            {data && <CountriesList data={data}/>}        
        </div>
     );
}
 
export default Home;
